const disableScroll = function () {
  let paddingOffset = window.innerWidth - document.body.offsetWidth + 'px'
  document.body.classList.toggle('scroll-lock')
  document.body.style.paddingRight = paddingOffset
}

const burger = document.querySelector('.menu-icon').addEventListener('click', function () {
  const menu = document.querySelector('.menu')
  this.classList.toggle('menu-icon__active')
  menu.classList.toggle('menu__active')
  disableScroll()

  let links = document.querySelectorAll('.menu__link').forEach(element => {
    element.onclick = () => {
      menu.classList.toggle('menu__active')
      this.classList.toggle('menu-icon__active')
      disableScroll()
    }
  })
})

$('.lookout__slider').slick({
  dots: true,
  infinite: true,
  speed: 300,
  slidesToShow: 1,
  centerMode: true,
  variableWidth: true
});

function arrowToHead() {
  let arrow = document.querySelector('.slick-next')
  let arrow2 = document.querySelector('.slick-prev')
  let h2 = document.querySelector('.lookout__title')
  h2.appendChild(arrow2)
  h2.appendChild(arrow)
}

arrowToHead()

const popupBtns = document.querySelectorAll('.rent-btn').forEach(el => {
  
  const form = document.querySelector('.popup')
  const done = document.querySelector('.done')
  const closeBtn = document.querySelector('.popup__close')
  const closeDone = document.querySelector('.done__close')
  let doneBtn = document.querySelector('.popup__button')
  const donePopup = document.querySelector('.done')


  el.addEventListener('click', () => {
    form.classList.toggle('form__active')
    closeBtn.onclick = () => form.classList.toggle('form__active')
    doneBtn.onclick = function(e) {
      e.preventDefault()
      donePopup.classList.toggle('form__active')
      closeDone.onclick = () => done.classList.toggle('form__active')
    }
  })
})




















